		act:0,
		index:'',
		datas:[]
	}
  }

  componentDidMount(){
	   this.refs.cakename.focus();
  }
  fSubmit= (e) =>{
	   e.preventDefault();
	   console.log('try');

	   let datas=this.state.datas;
	   let cakename=this.refs.cakename.value;

	   let data={
		   cakename
	   }

	   dats.push(data);

	   this.setState({
	    datas:datas
	   });

	   this.refs.myForm.reset()
  }
	render() {
		return(
		  <div className="App">
		  <h2>{this.state.title}</h2>
		  <form ref="myForm" className="myForm">
		     <input type="text" ref="cakename" placeholder="Enter cakename" className="formField" />
		      <button onclick={this.fSubmit} className="myButton"> submit</button>
		  </form>
		  <pre>

		 </pre>
		  </div>
		  );
	}
}
 export default App;
