import React,{ Component } from 'react';
import './App.css';

class App extends Component {
	constructor(props){
       super(props);
	this.state={
		title: 'React simple CRUD Application',
		act:0,
		index:'',
		datas:[]
	}
  }

  componentDidMount(){
	   this.refs.cakename.focus();
  }
  fSubmit= (e) =>{
	   e.preventDefault();
	   console.log('try');

	   let datas=this.state.datas;
	   let cakename=this.refs.cakename.value;

	   let data={
		   cakename
	   }

	   datas.push(data);

	   this.setState({
	    datas:datas
	   });

	   this.refs.myForm.reset();
	   this.refs.cakename.focus();
  }
	render() {
		let datas = this.state.datas;
		return(
		  <div className="App">
		  <h2>{this.state.title}</h2>
		  <form ref="myForm" className="myForm">
		     <input type="text" ref="cakename" placeholder="Enter cakename" className="formField" />
		      <button onclick={(i)=>this.fSubmit(i)} className="myButton"> submit</button>
		  </form>
		  <pre>
		  {
			  datas.map((data,i) =>
			   <li key={i} className="myList">
				   {i+1}.{data.cakename}
				   <button onclick={()=>this.fRemove(i)} className="myButton">remove</button>
				   <button onclick={()=>this.fEdit(i)} className="myButton">edit</button>
				</li>   
		  )}
		 </pre>
		  </div>
		  );
	}
}
 export default App;
